import './ScoreTracker.css';

import React, { useState } from 'react';

interface Player {
  id: number;
  name: string;
  totalScore: number;
}

interface ScoreTrackerProps {
  players: Player[];
  onScoreUpdate: (updatedPlayers: Player[]) => void;
}

const ScoreTracker: React.FC<ScoreTrackerProps> = ({ players, onScoreUpdate }) => {
  const [roundScores, setRoundScores] = useState<{ [key: number]: string }>({});
  const [operation, setOperation] = useState<'add' | 'subtract'>('add');

  const handleScoreChange = (playerId: number, value: string) => {
    setRoundScores(prev => ({
      ...prev,
      [playerId]: value
    }));
  };

  const submitScores = () => {
    const updatedPlayers = players.map(player => {
      const roundScore = parseInt(roundScores[player.id] || '0') || 0;
      return {
        ...player,
        totalScore: operation === 'add' 
          ? player.totalScore + roundScore
          : player.totalScore - roundScore
      };
    });

    onScoreUpdate(updatedPlayers);
    setRoundScores({}); // Reset round scores after submission
  };

  return (
    <div className="score-tracker">
      <div className="operation-toggle">
        <label>Score Operation:</label>
        <div className="toggle-buttons">
          <button
            className={`toggle-button ${operation === 'add' ? 'active' : ''}`}
            onClick={() => setOperation('add')}
          >
            Add (+)
          </button>
          <button
            className={`toggle-button ${operation === 'subtract' ? 'active' : ''}`}
            onClick={() => setOperation('subtract')}
          >
            Subtract (-)
          </button>
        </div>
      </div>

      <div className="score-inputs">
        {players.map(player => (
          <div key={player.id} className="score-input-group">
            <div className="player-info">
              <span className="player-name">{player.name || `Player ${player.id}`}</span>
              <span className="total-score">Total: {player.totalScore}</span>
            </div>
            <div className="score-input">
              <input
                type="number"
                value={roundScores[player.id] || ''}
                onChange={(e) => handleScoreChange(player.id, e.target.value)}
                placeholder="Enter score"
              />
            </div>
          </div>
        ))}
      </div>

      <button 
        className="submit-scores"
        onClick={submitScores}
      >
        Submit Round Scores
      </button>
    </div>
  );
};

export default ScoreTracker;
