import './App.css';
import 'react-toastify/dist/ReactToastify.css';
import './styles/toast.css';

import React from 'react';

import { ToastContainer } from 'react-toastify';

import PlayerSetup from './components/PlayerSetup';

const App: React.FC = () => {
  return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome to My Billard</h1>
        <p>A modern React TypeScript application</p>
      </header>
      <main>
        <PlayerSetup />
      </main>
      <ToastContainer
        position="top-center"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="colored"
        style={{
          width: '90%',
          maxWidth: '400px',
          fontSize: '14px',
          padding: '10px',
          margin: '0 auto',
        }}
      />
    </div>
  );
};

export default App;
